import React, { useState } from "react";
import { Link } from "react-router-dom"; // Import Link from react-router-dom
import "./Header.css";

function Header() {
  const [activeButton, setActiveButton] = useState("");

  const handleClick = (button) => {
    setActiveButton(button);
  };

  return (
    <header className="header">
      <nav className="navbar">
        <Link to="/" style={{ textDecoration: "none" }} className="logo">
          CoreOne
        </Link>
        <ul className="nav-links">
          <li>제품</li>
          <li
            className={`business-link ${activeButton === "business" ? "active" : ""}`}
            onClick={() => handleClick("business")}
          >
            <Link to="/business-registration" style={{ textDecoration: "none" }}>
              사업자 등록
            </Link>
          </li>
          <li>개발진</li>
        </ul>
        <div className="buttons">
          {/* Wrap the button with Link to navigate */}
          <Link to="/signup">
            <button
              className={`signup-btn ${activeButton === "signup" ? "active" : ""}`}
              onClick={() => handleClick("signup")}
            >
              회원가입
            </button>
          </Link>
          <Link to="/login">
            <button
              className={`login-btn ${activeButton === "login" ? "active" : ""}`}
              onClick={() => handleClick("login")}
            >
              로그인
            </button>
          </Link>
        </div>
      </nav>
    </header>
  );
}

export default Header;
